mapper.select:
SELECT count(id)
FROM user
WHERE id = #{id}

SELECT *
FROM user
WHERE id = #{id} AND password = #{password}

mapper.update:
update user
set password = #{password}, email = #{email}, loc_no = #{locNo}, age = #{age}, gender = #{gender}
where id = #{id}

update user
set name="", password = "", email = "", loc_no=1001, age = 0, gender = ''
where user_no = #{userNo}
